import React from 'react';
import { Link } from 'react-router-dom';
import HomeSection from '../HomeSection/HomeSection';

const Sach: React.FC = () => {
    const newsImage = `${process.env.PUBLIC_URL}/logo.jpg`; // Замените на нужное изображение

    return (
        <div style={{ marginBottom: '50px' }}>
            {/* Используем Link для создания кликабельного изображения */}
            <Link to="/new-page">
                <img src={newsImage} alt="Latest news in the coal industry" style={{ cursor: 'pointer', width: '100%', height: 'auto' }} />
            </Link>
            <HomeSection
                images={[newsImage]} // Передаем только URL изображения
                altText="Latest news in the coal industry" // Передаем alt текст
                title="Новости угольной промышленности"
                description="Здесь вы найдете свежие новости и обновления о событиях в угольной промышленности. Следите за обновлениями, чтобы быть в курсе последних событий и трендов."
                style={{ marginTop: '20px' }} // Добавляем отступ сверху
            />
        </div>
    );
};

export default Sach;
